CREATE TABLE IF NOT EXISTS public.wikimedia_events (id BIGINT NOT NULL, title VARCHAR(255) NOT NULL, title_url VARCHAR(255) NOT NULL, event_time VARCHAR(255) NOT NULL, "user" VARCHAR(255) NOT NULL, created_at TIMESTAMP WITHOUT TIME ZONE DEFAULT NOW(), updated_at TIMESTAMP WITHOUT TIME ZONE DEFAULT NOW(), CONSTRAINT wikimedia_events_pkey PRIMARY KEY (id));
CREATE TABLE IF NOT EXISTS public.h_wikimedia_events (id BIGINT NOT NULL, title VARCHAR(255) NOT NULL, title_url VARCHAR(255) NOT NULL, event_time VARCHAR(255) NOT NULL, "user" VARCHAR(255) NOT NULL, created_at TIMESTAMP WITHOUT TIME ZONE NOT NULL, updated_at TIMESTAMP WITHOUT TIME ZONE NOT NULL, operation VARCHAR(20) NOT NULL, partition_key VARCHAR(20) NOT NULL, CONSTRAINT h_wikimedia_events_pkey PRIMARY KEY (id));